#include <bits/stdc++.h>

#define int int64_t
#define sz(x) (int)(x.size())
#define ALL(x) (x).begin(), (x).end()
#define F0R(i, R) for (int i = (0); i < (R); ++i)
#define FOR(i, L, R) for (int i = (L); i <= (R); ++i)

using namespace std;

const string E =
"";

const string PI = 
"";

vector<int> odd, even;

void solve() {
	int N; cin >> N;
	if (N % 2 == 1) {
		cout << odd[N - 1] << '\n';
	} else {
		cout << even[N - 1] << '\n';
	}
}

int32_t main() {
	ios::sync_with_stdio(0);
	cin.tie(0);
	for (int i = 0; i < (int)PI.size(); i++) {
		int dig = PI[i] -'0';
		odd.push_back(dig % 2 == 1 ? 1: 0);
		even.push_back(dig % 2 == 0 ? 1: 0);
	}
	const int N = PI.size();
	for (int i = 1; i < N; i++) {
		odd[i] += odd[i - 1];
		even[i] += even[i - 1];
	}
	int T;
	cin >> T;
	while (T--) {
		solve();
	}
	return 0;
}
